import { getActiveClient } from '~/client/api';
import { pullFromCache } from '~/cache/api';
import { ingestInCache } from '~/cache/api/ingestInCache';
import { createEventSubscriber } from '~/core/events';
import { preparePayloadForCache } from '~/reaction/utils';

/**
 * ```js
 * import { onCommentReactionAdded } from '@amityco/ts-sdk'
 * const dispose = onCommentReactionAdded(comment => {
 *   // ...
 * })
 * ```
 *
 * Fired when a {@link Amity.Comment} has been reacted
 *
 * @param callback The function to call when the event was fired
 * @returns an {@link Amity.Unsubscriber} function to stop listening
 *
 * @category Comment Events
 */
export const onCommentReactionAdded = (
  callback: Amity.Listener<Amity.Comment>,
): Amity.Unsubscriber => {
  const client = getActiveClient();

  const filter = (payload: Amity.CommentPayload & { reactor: Amity.Reaction }) => {
    if (!client.cache) {
      callback(payload.comments[0]);
    } else {
      preparePayloadForCache('comment.addReaction', payload);
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      const { reactor, ...commentPayload } = payload;

      ingestInCache(commentPayload as Amity.CommentPayload);

      const comment = pullFromCache<Amity.Comment>([
        'comment',
        'get',
        payload.comments[0].commentId,
      ])!;

      callback(comment.data);
    }
  };

  return createEventSubscriber(client, 'comment.addReaction', 'comment.addReaction', filter);
};
