import { liveObject } from '~/utils/liveObject';
import { getMessage } from '../api';
import {
  onMessageUpdated,
  onMessageDeleted,
  onMessageFlagged,
  onMessageUnflagged,
  onMessageFlagCleared,
  onMessageReactionAdded,
  onMessageReactionRemoved,
} from '../events';
import { onMessageFetched } from '../events/onMessageFetched';

/**
 * ```js
 * import { liveMessage } from '@amityco/ts-sdk';
 *
 * let message;
 *
 * const unsubscribe = liveMessage(messageId, response => {
 *   message = response.data;
 * });
 * ```
 *
 * Observe all mutation on a given {@link Amity.Message}
 *
 * @param messageId the ID of the message to observe
 * @param callback the function to call when new data are available
 * @returns An {@link Amity.Unsubscriber} function to run when willing to stop observing the message
 *
 * @category Message Live Object
 */
export const liveMessage = (
  messageId: Amity.Message['messageId'],
  callback: Amity.LiveObjectCallback<Amity.Message>,
): Amity.Unsubscriber => {
  return liveObject(messageId, callback, 'messageId', getMessage, [
    onMessageFetched,
    onMessageUpdated,
    onMessageDeleted,
    onMessageFlagged,
    onMessageUnflagged,
    onMessageFlagCleared,
    onMessageReactionAdded,
    onMessageReactionRemoved,
  ]);
};
