import { getActiveClient } from '~/client/api';

import { pullFromCache } from '~/cache/api';
import { ingestInCache } from '~/cache/api/ingestInCache';
import { prepareMembershipPayload } from '~/group/utils';

/**
 * ```js
 * import { getPosts } from '@amityco/ts-sdk'
 * const { data: posts } = await getPosts(['foo', 'bar'])
 * ```
 *
 * Fetches a collection of {@link Amity.Post} objects
 *
 * @param postIds the IDs of the {@link Amity.Post} to fetch
 * @returns the associated collection of {@link Amity.Post} objects
 *
 * @category Post API
 * @async
 */
export const getPosts = async (
  postIds: Amity.Post['postId'][],
): Promise<Amity.Cached<Amity.Post[]>> => {
  const client = getActiveClient();
  client.log('post/getPosts', postIds);

  const encodedPostIds = postIds.map(postId => encodeURIComponent(postId));

  // API-FIX: endpoint should not be /list, parameters should be querystring.
  const { data: payload } = await client.http.get<Amity.PostPayload>(`/api/v3/posts/list`, {
    params: { postIds: encodedPostIds },
  });

  const data = prepareMembershipPayload(payload, 'communityUsers');

  const cachedAt = client.cache && Date.now();
  if (client.cache) ingestInCache(data, { cachedAt });

  return {
    data: data.posts,
    cachedAt,
  };
};

/**
 * ```js
 * import { getPosts } from '@amityco/ts-sdk'
 * const { data: posts } = getPosts.locally(['foo', 'bar'])
 * ```
 *
 * Fetches a collection of {@link Amity.Post} objects from cache
 *
 * @param postIds the IDs of the {@link Amity.Post} to fetch
 * @returns the associated collection of {@link Amity.Post} objects
 *
 * @category Post API
 */
getPosts.locally = (postIds: Amity.Post['postId'][]): Amity.Cached<Amity.Post[]> | undefined => {
  const client = getActiveClient();
  client.log('post/getPosts.locally', postIds);

  if (!client.cache) return;

  const cached = postIds
    .map(postId => pullFromCache<Amity.Post>(['post', 'get', postId])!)
    .filter(Boolean);

  const posts = cached.map(({ data }) => data);
  const oldest = cached.sort((a, b) => (a.cachedAt! < b.cachedAt! ? -1 : 1))?.[0];

  if (cached?.length < postIds.length) return;

  return {
    data: posts,
    cachedAt: oldest.cachedAt,
  };
};
