import { createPostEventSubscriber } from './utils';

/**
 * ```js
 * import { onPostFlagged } from '@amityco/ts-sdk'
 * const dispose = onPostFlagged(post => {
 *   // ...
 * })
 * ```
 *
 * Fired when a {@link Amity.Post} has been flagged
 *
 * @param callback The function to call when the event was fired
 * @returns an {@link Amity.Unsubscriber} function to stop listening
 *
 * @category Post Events
 */
export const onPostFlagged = (callback: Amity.Listener<Amity.Post>): Amity.Unsubscriber =>
  createPostEventSubscriber('post.flagged', callback);
