import { convertFromRaw } from '~/subChannel/utils';
import { channel1, date, user11 } from '.';

export function generateRawSubChannel(params?: Partial<Amity.RawSubChannel>): Amity.RawSubChannel {
  return {
    channelId: 'private-channel-id',
    channelPublicId: channel1.channelId,
    channelType: channel1.type,
    childCount: 0,
    createdAt: date,
    creatorId: 'private-user-id',
    creatorPublicId: user11.userId,
    isDeleted: false,
    lastMessageId: 'xxx',
    lastMessageTimestamp: date,
    messageFeedId: `${channel1.channelId}--sub-channel-id`,
    name: 'sub channel name',
    path: 'sub-channel-path',
    updatedAt: date,
    ...params,
  };
}

export function generateSubChannel(params?: Record<string, any>): Amity.SubChannel {
  return {
    ...convertFromRaw(generateRawSubChannel()),
    ...params,
  };
}
